mapper.select:



select * from member



			and instr(${type}, #{keyword})>0


		order by ${column} ${order}




select * from member



		and instr(member_no, #{keyword})>0


	order by ${column} ${order}



select * from club



			and instr(${type}, #{keyword})>0


		order by ${column} ${order}

mapper.update:
update mbti_survey
	set
		survey_question = #{surveyQuestion}, survey_answer1 = #{surveyAnswer1},
		survey_answer2 = #{surveyAnswer2} 
	where 
		survey_no = #{surveyNo}
