mapper.select:
  	update club_board
	set club_board_count = (
	select count(*) from club_board_reply where  club_board_no = #{clubBoardNo}
	)
	where club_board_no = #{clubBoardNo}

mapper.update:
update club_board
set club_board_like = (
select count(*) from club_board_like where club_board_no = #{clubBoardNo}
)
where club_board_no = #{clubBoardNo}

  	update club_board
	set club_board_reportcount = (
	select count(*) from club_board_report where club_report_target = #{clubBoardNo}
	)
	where club_board_no = #{clubBoardNo}

mapper.select:
select C.club_board_no, C.club_board_count, C.club_board_like, C.club_board_time, C.club_board_content, C.club_no, C.club_board_writer, C.club_board_reportcount, M.member_animal, M.member_no , M.member_nick, M.member_birth, M.member_interest1, M.member_interest2, M.member_interest3, M.member_gender, M.member_place1, M.member_place2, M.member_place3, M.member_sns_id, M.member_logindate, P.attach_no as 프로필사진번호, B.animal_no as 동물번호, A.attach_no as 동물사진번호, R.club_report_reporter
from club_board C left outer join member M on C.club_board_writer = M.member_no left outer join member_profile P on C.club_board_writer = P.member_no left outer join (select * from club_board_report where club_report_reporter = #{memberNo})R on C.club_board_no = R. club_report_target left outer join mbti_animal B on M.member_mbti = B.mbti left outer join animal_photo A on B.animal_no = A.animal_no
where C.club_board_No = #{clubBoardNo}

select C.club_board_no, C.club_board_count, C.club_board_like, C.club_board_time, C.club_board_content, C.club_no, C.club_board_writer, C.club_board_reportcount, M.member_animal, M.member_no , M.member_nick, M.member_interest1, M.member_interest2, M.member_interest3, M.member_gender, M.member_place1, M.member_place2, M.member_place3, M.member_sns_id, M.member_logindate, P.attach_no as 프로필사진번호
from club_board C left outer join member M on C.club_board_writer = M.member_no left outer join member_profile P on C.club_board_writer = P.member_no
where C.club_board_No = #{clubBoardNo}

select * from ( select rownum rn, TMP.* from ( select * FROM (select * from club_board where club_board_time >= TO_CHAR(SYSDATE-7,'YYYYMMDD' )) where club_no = #{clubNo} order by
club_board_like asc club_board_like desc club_board_count asc club_board_count desc club_board_no asc club_board_no desc club_board_readcount asc club_board_readcount desc
) TMP ) where rn between 1 and 10

mapper.update:
update club_board
set club_board_readcount = club_board_readcount+1
where club_board_no = #{clubBoardNo}
